// "Get Settings" node'undan al
const CC_raw  = $('If').first().json.CC
const BCC_raw = $('If').first().json.BCC;

function toCommaString(input) {
  let arr = [];

  // 1) Dizi ise direkt kullan
  if (Array.isArray(input)) {
    arr = input;
  } else if (typeof input === 'string') {
    const s = input.trim();

    // 2) Stringleştirilmiş JSON dizi mi? => parse et
    if ((s.startsWith('[') && s.endsWith(']')) || (s.startsWith('["') && s.endsWith('"]'))) {
      try {
        const parsed = JSON.parse(s);
        if (Array.isArray(parsed)) arr = parsed;
        else arr = [s];
      } catch {
        // JSON parse başarısızsa CSV gibi böl
        arr = s.split(/[,\s;]+/);
      }
    } else {
      // 3) Normal CSV / boşluk / ; ile böl
      arr = s.split(/[,\s;]+/);
    }
  } else if (input == null) {
    arr = [];
  } else {
    arr = [String(input)];
  }

  // 4) Her bir girdiden email'i çıkar (Name <mail@host> formatını da destekler)
  const emails = arr
    .map(v => String(v).trim())
    .map(v => {
      // <mail@host> varsa içini al
      const mAngle = v.match(/<([^>]+)>/);
      if (mAngle) return mAngle[1].trim();
      return v.replace(/^"+|"+$/g, ''); // çevredeki çift tırnakları temizle
    })
    // basit e-posta regex doğrulaması
    .map(v => {
      const m = v.match(/([A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,})/i);
      return m ? m[1] : '';
    })
    .filter(Boolean);

  // 5) Tekrarsızlaştır + virgül ile ve BOŞLUKSUZ birleştir
  const unique = Array.from(new Set(emails));
  return unique.join(',');
}

const CC  = toCommaString(CC_raw);
const BCC = toCommaString(BCC_raw);

return [{ json: { CC, BCC } }];

